mapper.select:
select id, number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount, remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time, estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status from `order`
and number like concat('%', #{number}, '%')
and phone like concat('%', #{phone}, '%')
and order_time between #{beginTime} and #{endTime}
and status = #{status}
order by order_time desc

select count(*)
from `order`


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(*) as count, date(order_time) as date
from `order`


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}


group by date(order_time)
